INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Thịt kho trứng', 'Thịt kho trứng ngon tuyệt', 40, 4, 'Easy', '[{"name":"Thịt ba chỉ","qty":"500g","note":"có mỡ nạc xen kẽ"},{"name":"Trứng gà","qty":"4 quả","note":"luộc chín, bóc vỏ"},{"name":"Nước mắm","qty":"3 muỗng canh","note":"nước mắm ngon"},{"name":"Đường","qty":"2 muỗng canh","note":"tạo màu và vị"},{"name":"Hành tím","qty":"2 củ","note":"băm nhỏ"},{"name":"Tiêu","qty":"1/2 muỗng cà phê","note":"rắc cuối"}]', '["Luộc trứng chín, bóc vỏ.","Thịt rửa sạch, cắt miếng vuông.","Ướp thịt với nước mắm, đường, tiêu 15 phút.","Phi hành tím cho thơm.","Cho thịt vào xào săn.","Thêm trứng và nước xâm xấp.","Kho nhỏ lửa đến khi nước sệt."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Cá kho tộ', 'Cá kho tộ ngon tuyệt', 35, 4, 'Easy', '[{"name":"Cá","qty":"600g","note":"cá lóc hoặc basa"},{"name":"Nước mắm","qty":"3 muỗng canh","note":"ướp và kho"},{"name":"Đường","qty":"1.5 muỗng canh","note":"thắng màu"},{"name":"Hành tím","qty":"2 củ","note":"băm"},{"name":"Tiêu","qty":"1/2 muỗng cà phê","note":"tạo mùi"}]', '["Cá làm sạch, cắt khúc.","Ướp cá với gia vị 15 phút.","Thắng đường đến màu cánh gián.","Xếp cá vào nồi, cho nước màu.","Thêm nước xâm xấp.","Kho lửa nhỏ 25 phút.","Rắc tiêu trước khi tắt bếp."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Gà kho gừng', 'Gà kho gừng ngon tuyệt', 30, 4, 'Easy', '[{"name":"Thịt gà","qty":"700g","note":"chặt miếng vừa ăn"},{"name":"Gừng","qty":"30g","note":"thái sợi"},{"name":"Nước mắm","qty":"2 muỗng canh","note":"ướp"},{"name":"Đường","qty":"1 muỗng canh","note":"cân vị"},{"name":"Tỏi","qty":"3 tép","note":"băm"}]', '["Ướp gà với gia vị 15 phút.","Phi tỏi cho thơm.","Cho gà vào xào săn.","Thêm gừng đảo đều.","Cho nước xâm xấp.","Kho nhỏ lửa 20 phút.","Nêm lại cho vừa."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Sườn xào chua ngọt', 'Sườn xào chua ngọt ngon tuyệt', 35, 4, 'Easy', '[{"name":"Sườn non","qty":"600g","note":"chặt khúc"},{"name":"Cà chua","qty":"2 quả","note":"cắt múi"},{"name":"Giấm","qty":"2 muỗng canh","note":"tạo chua"},{"name":"Đường","qty":"2 muỗng canh","note":"tạo ngọt"},{"name":"Nước mắm","qty":"2 muỗng canh","note":"nêm chính"}]', '["Luộc sườn sơ 5 phút.","Chiên sườn vàng đều.","Xào cà chua cho nhuyễn.","Thêm giấm, đường, nước mắm.","Cho sườn vào đảo.","Đun nhỏ lửa 10 phút.","Nêm lại."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Bò xào hành tây', 'Bò xào hành tây ngon tuyệt', 15, 4, 'Easy', '[{"name":"Thịt bò","qty":"400g","note":"thái mỏng ngang thớ"},{"name":"Hành tây","qty":"1 củ","note":"cắt múi"},{"name":"Tỏi","qty":"3 tép","note":"băm"},{"name":"Dầu hào","qty":"1.5 muỗng canh","note":"ướp bò"}]', '["Ướp bò 10 phút.","Phi tỏi cho thơm.","Xào bò nhanh tay.","Cho hành tây vào.","Đảo 1–2 phút.","Tắt bếp."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Rau muống xào tỏi', 'Rau muống xào tỏi ngon tuyệt', 10, 4, 'Easy', '[{"name":"Rau muống","qty":"300g","note":"nhặt, rửa sạch"},{"name":"Tỏi","qty":"4 tép","note":"đập dập"},{"name":"Muối","qty":"1/2 muỗng cà phê","note":"nêm"}]', '["Làm nóng chảo với dầu.","Phi tỏi cho thơm.","Cho rau vào xào nhanh.","Nêm muối.","Đảo đều và tắt bếp."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Thịt ba chỉ rang cháy cạnh', 'Thịt ba chỉ rang cháy cạnh ngon tuyệt', 20, 4, 'Easy', '[{"name":"Thịt ba chỉ","qty":"500g","note":"cắt lát mỏng"},{"name":"Nước mắm","qty":"2 muỗng canh","note":"nêm"},{"name":"Đường","qty":"1 muỗng canh","note":"tạo màu"}]', '["Cho thịt vào chảo rang khô.","Rang đến khi ra mỡ.","Thêm đường tạo màu.","Nêm nước mắm.","Đảo đến khi cháy cạnh."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Tôm rim mặn ngọt', 'Tôm rim mặn ngọt ngon tuyệt', 20, 4, 'Easy', '[{"name":"Tôm","qty":"400g","note":"lột vỏ, bỏ chỉ"},{"name":"Nước mắm","qty":"2 muỗng canh","note":"nêm"},{"name":"Đường","qty":"1.5 muỗng canh","note":"tạo ngọt"},{"name":"Tỏi","qty":"3 tép","note":"băm"}]', '["Phi tỏi cho thơm.","Cho tôm vào đảo đều.","Thêm nước mắm và đường.","Rim nhỏ lửa.","Khi sệt thì tắt bếp."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Trứng chiên thịt băm', 'Trứng chiên thịt băm ngon tuyệt', 10, 4, 'Easy', '[{"name":"Trứng gà","qty":"3 quả","note":"đánh tan"},{"name":"Thịt băm","qty":"200g","note":"ướp nhẹ"}]', '["Trộn trứng với thịt.","Nêm gia vị.","Đổ vào chảo nóng.","Chiên vàng hai mặt."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Canh trứng cà chua', 'Canh trứng cà chua ngon tuyệt', 15, 4, 'Easy', '[{"name":"Trứng gà","qty":"2 quả","note":"đánh tan"},{"name":"Cà chua","qty":"2 quả","note":"cắt múi"}]', '["Xào cà chua cho mềm.","Thêm nước.","Cho trứng vào khuấy nhẹ.","Nêm gia vị.","Tắt bếp."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Canh cải nấu thịt', 'Canh cải nấu thịt ngon tuyệt', 20, 4, 'Easy', '[{"name":"Rau cải","qty":"300g","note":"rửa sạch, cắt khúc"},{"name":"Thịt băm","qty":"200g","note":"ướp nhẹ muối tiêu"},{"name":"Hành tím","qty":"1 củ","note":"băm nhỏ"}]', '["Phi hành tím cho thơm.","Cho thịt băm vào xào tơi.","Thêm nước, đun sôi.","Cho rau cải vào.","Nêm gia vị, nấu thêm 3–5 phút.","Tắt bếp."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Đậu hũ sốt cà chua', 'Đậu hũ sốt cà chua ngon tuyệt', 15, 4, 'Easy', '[{"name":"Đậu hũ","qty":"3 miếng","note":"cắt vuông"},{"name":"Cà chua","qty":"3 quả","note":"băm nhuyễn"},{"name":"Hành tím","qty":"1 củ","note":"băm"}]', '["Chiên đậu hũ vàng các mặt.","Phi hành tím cho thơm.","Cho cà chua vào xào mềm.","Nêm gia vị vừa ăn.","Cho đậu vào sốt.","Đun nhỏ lửa 3–5 phút."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Canh khổ qua nhồi thịt', 'Canh khổ qua nhồi thịt ngon tuyệt', 40, 4, 'Easy', '[{"name":"Khổ qua","qty":"2 trái","note":"bỏ ruột"},{"name":"Thịt băm","qty":"300g","note":"trộn nấm mèo nếu có"},{"name":"Nấm mèo","qty":"20g","note":"ngâm nở, băm nhỏ"}]', '["Trộn thịt băm với nấm mèo.","Nhồi thịt vào khổ qua.","Đun nước sôi.","Cho khổ qua vào nấu.","Nấu lửa nhỏ 25–30 phút.","Nêm gia vị vừa ăn."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Cơm chiên trứng', 'Cơm chiên trứng ngon tuyệt', 10, 4, 'Easy', '[{"name":"Cơm nguội","qty":"300g","note":"để tơi"},{"name":"Trứng gà","qty":"2 quả","note":"đánh tan"},{"name":"Hành lá","qty":"2 nhánh","note":"cắt nhỏ"}]', '["Làm nóng chảo với dầu.","Cho trứng vào đảo nhanh.","Thêm cơm nguội.","Đảo đều cho tơi hạt.","Nêm gia vị.","Rắc hành lá, tắt bếp."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Cháo thịt băm', 'Cháo thịt băm ngon tuyệt', 30, 4, 'Easy', '[{"name":"Gạo","qty":"100g","note":"vo sạch"},{"name":"Thịt băm","qty":"150g","note":"ướp nhẹ"},{"name":"Hành lá","qty":"2 nhánh","note":"cắt nhỏ"}]', '["Nấu gạo với nước thành cháo.","Cho thịt băm vào khuấy đều.","Nấu thêm 10 phút.","Nêm gia vị.","Rắc hành lá.","Dùng nóng."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Ức gà áp chảo', 'Ức gà áp chảo ngon tuyệt', 20, 4, 'Easy', '[{"name":"Ức gà","qty":"500g","note":"đập mỏng đều"},{"name":"Dầu olive","qty":"1 muỗng canh","note":"áp chảo"},{"name":"Muối","qty":"1/2 muỗng cà phê","note":"ướp"}]', '["Ướp ức gà với muối tiêu 10 phút.","Làm nóng chảo với dầu olive.","Áp chảo mặt thứ nhất 7–8 phút.","Lật mặt còn lại, áp chảo tiếp.","Gà chín thì tắt bếp.","Cắt lát khi dùng."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Trứng luộc', 'Trứng luộc ngon tuyệt', 8, 4, 'Easy', '[{"name":"Trứng gà","qty":"2 quả","note":"rửa sạch"}]', '["Đun nước sôi.","Cho trứng vào nồi.","Luộc 7–8 phút.","Vớt ra ngâm nước lạnh.","Bóc vỏ khi dùng."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Cá hấp gừng', 'Cá hấp gừng ngon tuyệt', 25, 4, 'Easy', '[{"name":"Cá","qty":"600g","note":"làm sạch"},{"name":"Gừng","qty":"30g","note":"thái sợi"},{"name":"Hành lá","qty":"2 nhánh","note":"cắt khúc"}]', '["Ướp cá với muối nhẹ.","Xếp gừng lên cá.","Cho cá vào xửng hấp.","Hấp 20–25 phút.","Rắc hành lá.","Dùng nóng."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Rau luộc thập cẩm', 'Rau luộc thập cẩm ngon tuyệt', 10, 4, 'Easy', '[{"name":"Bông cải","qty":"200g","note":"cắt miếng"},{"name":"Cà rốt","qty":"100g","note":"cắt lát"},{"name":"Đậu que","qty":"100g","note":"cắt khúc"}]', '["Đun nước sôi.","Cho rau cứng vào trước.","Luộc 5–7 phút.","Vớt ra để ráo.","Dùng kèm nước chấm."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Cá chiên nước mắm', 'Cá chiên nước mắm ngon tuyệt', 20, 4, 'Easy', '[{"name":"Cá","qty":"600g","note":"làm sạch, để ráo"},{"name":"Nước mắm","qty":"2 muỗng canh","note":"làm sốt"},{"name":"Tỏi","qty":"3 tép","note":"băm nhỏ"}]', '["Chiên cá vàng giòn hai mặt.","Phi tỏi cho thơm.","Cho nước mắm vào đun nhẹ.","Rưới sốt lên cá.","Tắt bếp và dùng."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Canh chua cá', 'Canh chua cá ngon tuyệt', 35, 4, 'Easy', '[{"name":"Cá","qty":"500g","note":"cá lóc hoặc cá basa, cắt khúc"},{"name":"Cà chua","qty":"2 quả","note":"cắt múi cau"},{"name":"Me","qty":"30g","note":"dầm lấy nước chua"},{"name":"Hành lá","qty":"2 nhánh","note":"cắt khúc"},{"name":"Rau thơm","qty":"50g","note":"ngò gai, rau om"}]', '["Đun sôi nước trong nồi.","Cho nước me vào tạo vị chua.","Thả cá vào nấu chín.","Cho cà chua vào nấu mềm.","Nêm nước mắm, muối cho vừa.","Cho hành lá và rau thơm.","Tắt bếp và dùng nóng."]', 1, NOW());
INSERT INTO recipes (name, description, cook_time, servings, difficulty, ingredients, steps, active, created_at) VALUES ('Canh chua tôm', 'Canh chua tôm ngon tuyệt', 30, 4, 'Easy', '[{"name":"Tôm","qty":"400g","note":"lột vỏ, rút chỉ lưng"},{"name":"Cà chua","qty":"2 quả","note":"cắt múi"},{"name":"Me","qty":"30g","note":"lấy nước chua"},{"name":"Hành lá","qty":"2 nhánh","note":"cắt khúc"}]', '["Đun sôi nước.","Cho nước me vào nồi.","Cho tôm vào nấu chín.","Thêm cà chua.","Nêm gia vị vừa ăn.","Cho hành lá vào.","Tắt bếp."]', 1, NOW());
